mapper.select:
SELECT N.ID, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_CREATED_DATE, NOTICE_UPDATED_DATE, NOTICE_VIEWED_COUNT, USER_IDENTIFICATION FROM ( SELECT ROWNUM R, N.ID, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_CREATED_DATE, NOTICE_UPDATED_DATE, NOTICE_VIEWED_COUNT, USER_IDENTIFICATION FROM TBL_USER U JOIN TBL_NOTICE N
ON U.ID = N.ADMIN_ID AND ROWNUM <= #{pagination.page} * #{pagination.rowCount}
ORDER BY
NOTICE_VIEWED_COUNT
N.ID
ASC
) N WHERE R > (#{pagination.page} - 1) * #{pagination.rowCount}

 SELECT N.ID, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_CREATED_DATE, NOTICE_UPDATED_DATE,  NOTICE_VIEWED_COUNT, ADMIN_ID, NOTICE_IMAGE_NAME, NOTICE_IMAGE_UUID, NOTICE_IMAGE_SIZE
FROM TBL_USER U JOIN TBL_NOTICE N
ON U.ID = N.ADMIN_ID AND N.ID = #{id}

mapper.update:
UPDATE TBL_NOTICE
SET NOTICE_TITLE = #{noticeTitle}, NOTICE_CONTENT = #{noticeContent}, NOTICE_UPDATED_DATE = SYSDATE
WHERE ID = #{id}
