mapper.select:
SELECT ID, PRODUCT_NAME, PRODUCT_DETAIL, PRODUCT_COST, PRODUCT_STOCK, PRODUCT_REGISTER_DATE, PRODUCT_UPDATED_DATE, PRODUCT_CATEGORY, PRODUCT_USED_LOCATION, PRODUCT_IMAGE_NAME, PRODUCT_IMAGE_UUID, PRODUCT_IMAGE_SIZE,PRODUCT_IMAGE_PATH
FROM TBL_PRODUCT
WHERE ID = #{productId}

SELECT R ,TP.ID, PRODUCT_NAME, PRODUCT_DETAIL, PRODUCT_COST, PRODUCT_STOCK, PRODUCT_REGISTER_DATE, PRODUCT_UPDATED_DATE, PRODUCT_CATEGORY, PRODUCT_USED_LOCATION, PRODUCT_IMAGE_NAME, PRODUCT_IMAGE_UUID, PRODUCT_IMAGE_SIZE,USER_WISH_COUNT FROM ( SELECT ROWNUM R ,TP.ID, PRODUCT_NAME, PRODUCT_DETAIL, PRODUCT_COST, PRODUCT_STOCK, PRODUCT_REGISTER_DATE, PRODUCT_UPDATED_DATE, PRODUCT_CATEGORY, PRODUCT_USED_LOCATION, PRODUCT_IMAGE_NAME, PRODUCT_IMAGE_UUID, PRODUCT_IMAGE_SIZE,USER_WISH_COUNT FROM ( SELECT TP.ID, PRODUCT_NAME, PRODUCT_DETAIL, PRODUCT_COST, PRODUCT_STOCK, PRODUCT_REGISTER_DATE, PRODUCT_UPDATED_DATE, PRODUCT_CATEGORY, PRODUCT_USED_LOCATION, PRODUCT_IMAGE_NAME, PRODUCT_IMAGE_UUID, PRODUCT_IMAGE_SIZE,COUNT(USER_ID) USER_WISH_COUNT FROM TBL_PRODUCT TP LEFT OUTER JOIN TBL_PRODUCT_WISH TPW ON TP.ID = TPW.PRODUCT_ID GROUP BY TP.ID, PRODUCT_NAME, PRODUCT_DETAIL, PRODUCT_COST, PRODUCT_STOCK, PRODUCT_REGISTER_DATE, PRODUCT_UPDATED_DATE, PRODUCT_CATEGORY, PRODUCT_USED_LOCATION, PRODUCT_IMAGE_NAME, PRODUCT_IMAGE_UUID, PRODUCT_IMAGE_SIZE
ORDER BY ID DESC
ORDER BY USER_WISH_COUNT DESC
) TP
WHERE TP.PRODUCT_CATEGORY =#{category}
WHERE TP.PRODUCT_USED_LOCATION LIKE '%'||#{location}||'%'
WHERE TP.PRODUCT_CATEGORY =#{category} AND TP.PRODUCT_USED_LOCATION LIKE '%'||#{location}||'%'
)TP
WHERE R>=#{count} AND R<#{count}+16
